package scarlet.machado.lmsapp

object ProdutoService {

    fun getProdutos(): List<Produto> {

        var produtos = mutableListOf<Produto>()

        for (i in 1..3) {
            val d = Produto()
            d.nome = "Produto $i"
            d.valor = "Valor $i"
            d.descricao = "Descrição $i"
            d.foto = "data:image/png;base64,"
            produtos.add(d)
        }

        return produtos
    }
}